async function pageFunction(context) {
    const { page, request, log } = context;

    if (!request.url.includes('/job/')) {
        log.info(`Listing page: ${request.url}`);
        await new Promise(r => setTimeout(r, 3000));
        return null;
    }

    log.info(`Extracting job: ${request.url}`);
    await new Promise(r => setTimeout(r, 2000));

    const jobData = await page.evaluate(() => {
        const result = {};

        // Helper to decode HTML entities
        const decodeHtml = (html) => {
            const txt = document.createElement('textarea');
            txt.innerHTML = html;
            return txt.value;
        };

        // Extract from JSON-LD structured data
        const jsonLdScript = document.querySelector('script[type="application/ld+json"]');
        if (jsonLdScript) {
            try {
                const jsonLd = JSON.parse(jsonLdScript.textContent);
                if (jsonLd['@type'] === 'JobPosting') {
                    result.jobTitle = jsonLd.title || null;

                    // Clean description - only remove EEO boilerplate at the END
                    let desc = jsonLd.description || '';

                    // Decode HTML entities
                    desc = decodeHtml(desc);

                    // Remove EEO boilerplate (only at the end)
                    const eeoIndex = desc.indexOf('EEO Employer');
                    if (eeoIndex > 0) {
                        desc = desc.substring(0, eeoIndex);
                    }
                    const eeoIndex2 = desc.indexOf('Apex Systems is an equal opportunity employer');
                    if (eeoIndex2 > 0) {
                        desc = desc.substring(0, eeoIndex2);
                    }

                    // Remove recruiter intro ONLY if it's at the very beginning
                    // Pattern: "Apex Systems is a world class...@apexsystems.com. Here are the details:"
                    desc = desc.replace(/^Apex Systems is a world class[^]*?Here are the details:\s*/i, '');

                    // Remove single-line recruiter contact sentences (not multi-line)
                    // Only match specific patterns that are clearly just contact info
                    desc = desc.replace(/^To apply,? email [^\n]{0,50}@apexsystems\.com[^\n]*\n?/i, '');
                    desc = desc.replace(/^For applicants[^\n]{0,100}@apexsystems\.com[^\n]*\n?/i, '');

                    result.description = desc.trim();

                    result.employmentType = jsonLd.employmentType || null;
                    result.datePosted = jsonLd.datePosted || null;
                    if (jsonLd.identifier && jsonLd.identifier.value) {
                        result.jobNumber = String(jsonLd.identifier.value);
                    }
                    if (jsonLd.jobLocation && jsonLd.jobLocation.address) {
                        const addr = jsonLd.jobLocation.address;
                        result.location = [addr.addressLocality, addr.addressRegion].filter(Boolean).join(', ');
                    }
                }
            } catch (e) { }
        }

        // Get additional fields from body text
        const bodyText = document.body.innerText;

        const extractField = (patterns) => {
            for (const pattern of patterns) {
                const match = bodyText.match(pattern);
                if (match) return match[1].trim().split('\n')[0];
            }
            return null;
        };

        // Security clearance - multiple patterns
        result.securityClearance = extractField([
            /Security Clearance:\s*([^\n]{5,80})/i,
            /Clearance requirement:\s*([^\n]{5,80})/i,
            /Clearance[^:]*:\s*((?:Active\s+)?(?:TS\/SCI|Top Secret|Secret|Public Trust)[^\n]{0,60})/i,
            /((?:Top Secret|TS\/SCI|Secret)\s+clearance[^\n]{0,40}(?:required|preferred|acceptable)[^\n]{0,30})/i,
            /((?:Active|Current)\s+(?:Top Secret|TS\/SCI|Secret)[^\n]{0,40})/i
        ]);

        // Pay rate - look for various patterns
        result.payRate = extractField([
            /Pay Rate:\s*([^\n]+)/i, 
            /Rate:\s*(\$[^\n]+|Negotiable[^\n]*)/i,
            /Pay:\s*(\$[^\n]+)/i,
            /Base Salary[:\s]*(\$?[\d,]+-?[\d,]*(?:k|K)?)/i,
            /Salary[:\s]*(\$?[\d,]+-?[\d,]*(?:k|K)?)/i
        ]);

        // Duration
        result.duration = extractField([
            /Project Duration:\s*([^\n]+)/i, 
            /Duration:\s*([^\n]+)/i, 
            /Contract Length:\s*([^\n]+)/i
        ]);

        return result;
    });

    return { url: request.url, ...jobData, scrapedAt: new Date().toISOString() };
}
