    25→/**
    26→ * Main service for orchestrating Git synchronization operations.
    27→ * Handles async sync processing, file generation, and history tracking.
    28→ *
    29→ * Key Features:
    30→ * - Async processing to avoid blocking API responses
    31→ * - Retry logic with exponential backoff for transient failures
    32→ * - Rate limiting awareness to avoid hitting Git API limits
    33→ * - Comprehensive error handling (Git failures don't block publish workflow)
    34→ */
    35→@Service
    36→@RequiredArgsConstructor
    37→@Slf4j
    38→public class GitSyncService {
    39→
    40→    private final TenantGitConfigRepository gitConfigRepository;
    41→    private final GitSyncHistoryRepository syncHistoryRepository;
    42→    private final TemplatePackageRepository packageRepository;
    43→    private final PackageVersionRepository versionRepository;
    44→    private final PackageTemplateRepository templateRepository;
    45→    private final TenantRepository tenantRepository;
    46→    private final BlobStorageService blobStorageService;
    47→    private final GitProviderFactory gitProviderFactory;
    48→
    49→    private static final DateTimeFormatter DATE_FORMATTER = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")
    50→            .withZone(ZoneOffset.UTC);
    51→
    52→    // ========================================
    53→    // Sync Queue Operations
    54→    // ========================================
    55→
    56→    /**
    57→     * Queues a sync job and returns immediately.
    58→     * The sync is performed asynchronously.
    59→     *
    60→     * @param tenantId    the tenant identifier
    61→     * @param packageId   the template package identifier
    62→     * @param versionId   the package version identifier
    63→     * @param syncType    the type of sync operation
    64→     * @param triggeredBy the user who triggered the sync
    65→     * @return the sync history ID for tracking
    66→     */
    67→    @Transactional
    68→    public String queueSync(UUID tenantId, UUID packageId, UUID versionId,
    69→            GitSyncHistory.SyncType syncType, UUID triggeredBy) {
    70→
    71→        // Validate Git config exists and is enabled
    72→        Optional<TenantGitConfig> configOpt = gitConfigRepository.findByTenantId(tenantId);
    73→        if (configOpt.isEmpty()) {
    74→            log.debug("Git sync skipped for tenant {} - no configuration", tenantId);
    75→            return null;
    76→        }
    77→
    78→        TenantGitConfig config = configOpt.get();
    79→        if (!config.isSyncEnabled() || !config.isActive()) {
    80→            log.debug("Git sync skipped for tenant {} - sync disabled", tenantId);
    81→            return null;
    82→        }
    83→
    84→        // Check if sync should happen for this type
    85→        if (syncType == GitSyncHistory.SyncType.PUBLISH && !config.isSyncOnPublish()) {
    86→            log.debug("Git sync skipped for tenant {} - sync on publish disabled", tenantId);
    87→            return null;
    88→        }
    89→
    90→        // Check for in-progress sync to avoid concurrent operations
    91→        List<GitSyncHistory> inProgress = syncHistoryRepository.findInProgressByConfigId(config.getId());
    92→        if (!inProgress.isEmpty()) {
    93→            log.warn("Git sync already in progress for tenant {} - queueing", tenantId);
    94→            // Could implement a queue here, for now just skip
    95→            return null;
    96→        }
    97→
    98→        // Load entities
    99→        Tenant tenant = tenantRepository.findById(tenantId)
   100→                .orElseThrow(() -> new IllegalArgumentException("Tenant not found: " + tenantId));
   101→
   102→        TemplatePackage pkg = packageId != null ? packageRepository.findById(packageId).orElse(null) : null;
   103→        PackageVersion version = versionId != null ? versionRepository.findById(versionId).orElse(null) : null;
   104→
   105→        // Create sync history record
   106→        GitSyncHistory syncHistory = GitSyncHistory.builder()
   107→                .tenant(tenant)
   108→                .gitConfig(config)
   109→                .templatePackage(pkg)
   110→                .packageVersion(version)
   111→                .syncType(syncType)
   112→                .status(GitSyncHistory.SyncStatus.PENDING)
   113→                .startedAt(Instant.now())
   114→                .triggeredBy(triggeredBy)
   115→                .triggerReason(buildTriggerReason(syncType, pkg, version))
   116→                .gitBranch(config.getDefaultBranch())
   117→                .build();
   118→
   119→        GitSyncHistory savedHistory = syncHistoryRepository.save(syncHistory);
   120→
   121→        // Trigger async sync
   122→        performSyncAsync(savedHistory.getId());
   123→
   124→        return savedHistory.getId().toString();
   125→    }
   126→
   127→    /**
   128→     * Performs the actual sync operation asynchronously.
   129→     * This method is called after the sync is queued.
   130→     *
   131→     * @param syncId the sync history ID
   132→     */
   133→    @Async("gitSyncExecutor")
   134→    @Retryable(
   135→            retryFor = {GitProviderException.class},
   136→            maxAttempts = 3,
   137→            backoff = @Backoff(delay = 1000, multiplier = 2, maxDelay = 10000)
   138→    )
   139→    public CompletableFuture<Void> performSyncAsync(UUID syncId) {
   140→        try {
   141→            performSync(syncId);
   142→            return CompletableFuture.completedFuture(null);
   143→        } catch (Exception e) {
   144→            log.error("Async sync failed for syncId: {}", syncId, e);
   145→            return CompletableFuture.failedFuture(e);
   146→        }
   147→    }
   148→
   149→    /**
   150→     * Performs the actual sync operation.
   151→     * Can be called synchronously or from async worker.
   152→     *
   153→     * @param syncId the sync history ID
   154→     */
   155→    @Transactional
   156→    public void performSync(UUID syncId) {
   157→        GitSyncHistory syncHistory = syncHistoryRepository.findById(syncId)
   158→                .orElseThrow(() -> new IllegalArgumentException("Sync history not found: " + syncId));
   159→
   160→        // Mark as in progress
   161→        syncHistory.start();
   162→        syncHistoryRepository.save(syncHistory);
   163→
   164→        try {
   165→            TenantGitConfig config = syncHistory.getGitConfig();
   166→            TemplatePackage pkg = syncHistory.getTemplatePackage();
   167→            PackageVersion version = syncHistory.getPackageVersion();
   168→
   169→            // Create Git provider
   170→            GitProvider provider = gitProviderFactory.createProvider(config);
   171→
   172→            // Build files to sync
   173→            List<GitFile> files = buildGitFiles(
   174→                    syncHistory.getTenant().getId(),
   175→                    pkg,
   176→                    version,
   177→                    version != null ? templateRepository.findByPackageVersionIdOrderByTemplatePath(version.getId())
   178→                            : List.of());
   179→
   180→            if (files.isEmpty()) {
   181→                log.info("No files to sync for syncId: {}", syncId);
   182→                syncHistory.complete(null, 0);
   183→                syncHistoryRepository.save(syncHistory);
   184→                return;
   185→            }
   186→
   187→            // Build commit message
   188→            String commitMessage = buildCommitMessage(pkg, version, syncHistory.getSyncType());
   189→
   190→            // Commit and push files
   191→            String commitSha = provider.commitAndPush(files, commitMessage);
   192→            syncHistory.setGitCommitSha(commitSha);
   193→
   194→            // Create tag if configured
   195→            if (version != null && config.getBranchStrategy() == TenantGitConfig.BranchStrategy.VERSION_TAGS) {
   196→                String tagName = "v" + version.getVersion();
   197→                if (pkg != null) {
   198→                    tagName = pkg.getPackageName() + "-" + tagName;
   199→                }
   200→                try {
   201→                    provider.createTag(tagName, "Released " + version.getVersion());
   202→                    syncHistory.setGitTag(tagName);
   203→                } catch (GitProviderException e) {
   204→                    // Tag might already exist, log and continue
   205→                    log.warn("Failed to create tag {}: {}", tagName, e.getMessage());
   206→                }
   207→            }
   208→
   209→            // Create branch if configured
   210→            if (version != null && config.getBranchStrategy() == TenantGitConfig.BranchStrategy.VERSION_BRANCHES) {
   211→                String branchName = "version/" + version.getVersion();
   212→                if (pkg != null) {
   213→                    branchName = pkg.getPackageName() + "/" + branchName;
