    92→                String fetchedDefaultBranch = repoInfo.path("default_branch").asText("main");
    93→                boolean hasWriteAccess = repoInfo.path("permissions").path("push").asBoolean(false);
    94→
    95→                return GitConnectionTestResult.builder()
    96→                        .success(true)
    97→                        .provider(PROVIDER_NAME)
    98→                        .repoUrl(repoUrl)
    99→                        .repoExists(true)
