    31→@Service
    32→@RequiredArgsConstructor
    33→@Slf4j
    34→public class GitImportService {
    35→
    36→    private final TenantGitConfigRepository gitConfigRepository;
    37→    private final GitSyncHistoryRepository syncHistoryRepository;
    38→    private final TemplatePackageRepository packageRepository;
    39→    private final PackageVersionRepository versionRepository;
    40→    private final PackageTemplateRepository templateRepository;
    41→    private final TenantRepository tenantRepository;
    42→    private final BlobStorageService blobStorageService;
