    44→
    45→    // ========================================
    46→    // Import Queue Operations
    47→    // ========================================
    48→
    49→    /**
    50→     * Queues an import job and returns immediately.
    51→     * The import is performed asynchronously.
    52→     *
    53→     * @param tenantId    the tenant identifier
    54→     * @param packageId   optional package identifier to limit import scope
    55→     * @param versionId   optional version identifier to limit import scope
    56→     * @param branchName  optional branch name (uses default branch if null)
    57→     * @param triggeredBy the user who triggered the import
    58→     * @return the sync history ID for tracking, or null if import cannot proceed
    59→     */
    60→    @Transactional
    61→    public String queueImport(UUID tenantId, UUID packageId, UUID versionId,
    62→            String branchName, UUID triggeredBy) {
    63→
    64→        // Validate Git config exists and is enabled
    65→        Optional<TenantGitConfig> configOpt = gitConfigRepository.findByTenantId(tenantId);
    66→        if (configOpt.isEmpty()) {
    67→            log.debug("Git import skipped for tenant {} - no configuration", tenantId);
    68→            return null;
    69→        }
    70→
    71→        TenantGitConfig config = configOpt.get();
    72→        if (!config.isActive()) {
    73→            log.debug("Git import skipped for tenant {} - config not active", tenantId);
    74→            return null;
    75→        }
    76→
    77→        // Check for in-progress import to avoid concurrent operations
    78→        List<GitSyncHistory> inProgress = syncHistoryRepository.findInProgressByConfigId(config.getId());
    79→        if (!inProgress.isEmpty()) {
    80→            log.warn("Git import already in progress for tenant {} - skipping", tenantId);
    81→            return null;
    82→        }
    83→
    84→        // Load entities
    85→        Tenant tenant = tenantRepository.findById(tenantId)
    86→                .orElseThrow(() -> new IllegalArgumentException("Tenant not found: " + tenantId));
    87→
    88→        TemplatePackage pkg = packageId != null ? packageRepository.findById(packageId).orElse(null) : null;
    89→        PackageVersion version = versionId != null ? versionRepository.findById(versionId).orElse(null) : null;
    90→
    91→        // Determine branch to import from
    92→        String targetBranch = branchName != null && !branchName.isBlank()
    93→                ? branchName
    94→                : config.getDefaultBranch();
    95→
    96→        // Create sync history record (reusing GitSyncHistory for import tracking)
    97→        GitSyncHistory importHistory = GitSyncHistory.builder()
    98→                .tenant(tenant)
    99→                .gitConfig(config)
   100→                .templatePackage(pkg)
   101→                .packageVersion(version)
   102→                .syncType(GitSyncHistory.SyncType.MANUAL) // Using MANUAL for imports
   103→                .status(GitSyncHistory.SyncStatus.PENDING)
   104→                .startedAt(Instant.now())
   105→                .triggeredBy(triggeredBy)
   106→                .triggerReason(buildTriggerReason(pkg, version, targetBranch))
   107→                .gitBranch(targetBranch)
   108→                .build();
   109→
   110→        GitSyncHistory savedHistory = syncHistoryRepository.save(importHistory);
   111→
   112→        // Trigger async import
   113→        performImportAsync(savedHistory.getId());
   114→
   115→        return savedHistory.getId().toString();
   116→    }
   117→
   118→    /**
   119→     * Performs the actual import operation asynchronously.
   120→     * This method is called after the import is queued.
   121→     *
   122→     * @param importId the sync history ID for this import
   123→     */
   124→    @Async("gitSyncExecutor")
   125→    @Retryable(
   126→            retryFor = {GitProviderException.class},
   127→            maxAttempts = 3,
   128→            backoff = @Backoff(delay = 1000, multiplier = 2, maxDelay = 10000)
   129→    )
   130→    public CompletableFuture<Void> performImportAsync(UUID importId) {
   131→        try {
   132→            performImport(importId);
   133→            return CompletableFuture.completedFuture(null);
   134→        } catch (Exception e) {
   135→            log.error("Async import failed for importId: {}", importId, e);
   136→            return CompletableFuture.failedFuture(e);
   137→        }
   138→    }
