   170→            }
   171→
   172→            // Get latest commit SHA for tracking
   173→            String commitSha = provider.getLatestCommitSha();
   174→            importHistory.setGitCommitSha(commitSha);
   175→
   176→            // Process files and detect conflicts
   177→            ImportResult result = processImportedFiles(
   178→                    importHistory.getTenant().getId(),
   179→                    gitFiles,
   180→                    importHistory.getTemplatePackage(),
   181→                    importHistory.getPackageVersion());
   182→
   183→            // Save results
   184→            importHistory.complete(commitSha, result.filesImported);
   185→            if (!result.conflicts.isEmpty()) {
   186→                log.warn("Import completed with {} conflicts for importId: {}",
   187→                        result.conflicts.size(), importId);
