    33→public class GitImportService {
    34→
    35→    private final TenantGitConfigRepository gitConfigRepository;
    36→    private final GitSyncHistoryRepository syncHistoryRepository;
    37→    private final TenantRepository tenantRepository;
    38→
    39→    /**
    40→     * Queues an import job and returns immediately.
    41→     *
    42→     * @param tenantId the tenant identifier
    43→     * @param request  the import request
    44→     * @param userId   the user who triggered the import
    45→     * @return the import operation response
    46→     */
    47→    @Transactional
    48→    public GitImportResponse queueImport(UUID tenantId, GitImportRequest request, UUID userId) {
    49→        Optional<TenantGitConfig> configOpt = gitConfigRepository.findByTenantId(tenantId);
    50→        if (configOpt.isEmpty()) {
    51→            log.debug("Git import skipped for tenant {} - no configuration", tenantId);
    52→            return GitImportResponse.failure("No Git configuration found for tenant");
    53→        }
    54→
    55→        TenantGitConfig config = configOpt.get();
    56→        if (!config.isActive()) {
    57→            log.debug("Git import skipped for tenant {} - config inactive", tenantId);
    58→            return GitImportResponse.failure("Git configuration is not active");
    59→        }
    60→
    61→        List<GitSyncHistory> inProgress = syncHistoryRepository.findInProgressByConfigId(config.getId());
    62→        if (!inProgress.isEmpty()) {
    63→            log.warn("Git import already in progress for tenant {} - queueing", tenantId);
    64→            return GitImportResponse.failure("Import already in progress");
    65→        }
    66→
    67→        Tenant tenant = tenantRepository.findById(tenantId)
    68→                .orElseThrow(() -> new IllegalArgumentException("Tenant not found: " + tenantId));
    69→
    70→        GitSyncHistory importHistory = GitSyncHistory.builder()
    71→                .tenant(tenant)
    72→                .gitConfig(config)
    73→                .templatePackage(null)
    74→                .packageVersion(null)
    75→                .syncType(GitSyncHistory.SyncType.MANUAL)
    76→                .status(GitSyncHistory.SyncStatus.PENDING)
    77→                .startedAt(Instant.now())
    78→                .triggeredBy(userId)
    79→                .triggerReason("Git import from branch: " + request.getBranchName())
    80→                .gitBranch(request.getBranchName())
    81→                .build();
    82→
    83→        GitSyncHistory savedHistory = syncHistoryRepository.save(importHistory);
    84→        performImportAsync(savedHistory.getId(), request);
    85→
    86→        return GitImportResponse.pending(savedHistory.getId(), "Import operation queued successfully");
    87→    }
