    19→/**
    20→ * Service for encrypting and decrypting sensitive data.
    21→ * Uses AES-256-GCM for authenticated encryption.
    22→ *
    23→ * In production, the encryption key should be retrieved from Azure Key Vault
    24→ * or another secure key management system.
    25→ */
    26→@Service
    27→@Slf4j
    28→public class EncryptionService {
    29→
    30→    private static final String ALGORITHM = "AES/GCM/NoPadding";
    31→    private static final int GCM_IV_LENGTH = 12; // 96 bits
    32→    private static final int GCM_TAG_LENGTH = 128; // 128 bits
    33→
    34→    @Value("${app.encryption.key:#{null}}")
    35→    private String encryptionKeyBase64;
    36→
    37→    @Value("${app.encryption.enabled:true}")
    38→    private boolean encryptionEnabled;
    39→
    40→    private SecretKey secretKey;
    41→    private final SecureRandom secureRandom = new SecureRandom();
    42→
    43→    @PostConstruct
    44→    public void init() {
    45→        if (encryptionKeyBase64 == null || encryptionKeyBase64.isBlank()) {
    46→            log.warn("Encryption key not configured. Using default key for development. " +
    47→                    "This is NOT secure for production use!");
    48→            // Generate a default key for development - 256 bits (32 bytes)
    49→            byte[] defaultKey = new byte[32];
    50→            // Use a fixed seed for development reproducibility
    51→            new SecureRandom("fhirmapper-dev-key".getBytes()).nextBytes(defaultKey);
    52→            secretKey = new SecretKeySpec(defaultKey, "AES");
    53→        } else {
    54→            try {
    55→                byte[] keyBytes = Base64.getDecoder().decode(encryptionKeyBase64);
    56→                if (keyBytes.length != 32) {
    57→                    throw new IllegalArgumentException("Encryption key must be 256 bits (32 bytes)");
    58→                }
    59→                secretKey = new SecretKeySpec(keyBytes, "AES");
    60→                log.info("Encryption service initialized with configured key");
    61→            } catch (IllegalArgumentException e) {
    62→                log.error("Invalid encryption key configuration", e);
    63→                throw new EncryptionException("Failed to initialize encryption service", e);
    64→            }
    65→        }
    66→    }
    67→
    68→    /**
    69→     * Encrypts a plain text string using AES-256-GCM.
    70→     * The result includes the IV prepended to the ciphertext.
    71→     *
    72→     * @param plainText the text to encrypt
    73→     * @return the encrypted data as bytes (IV + ciphertext)
    74→     * @throws EncryptionException if encryption fails
    75→     */
    76→    public byte[] encrypt(String plainText) {
    77→        if (plainText == null) {
    78→            return null;
    79→        }
    80→
    81→        if (!encryptionEnabled) {
    82→            // Return plain bytes when encryption is disabled (dev mode)
    83→            return plainText.getBytes(StandardCharsets.UTF_8);
    84→        }
    85→
    86→        try {
    87→            // Generate a random IV
    88→            byte[] iv = new byte[GCM_IV_LENGTH];
    89→            secureRandom.nextBytes(iv);
    90→
    91→            // Initialize cipher
    92→            Cipher cipher = Cipher.getInstance(ALGORITHM);
    93→            GCMParameterSpec parameterSpec = new GCMParameterSpec(GCM_TAG_LENGTH, iv);
    94→            cipher.init(Cipher.ENCRYPT_MODE, secretKey, parameterSpec);
    95→
    96→            // Encrypt
    97→            byte[] cipherText = cipher.doFinal(plainText.getBytes(StandardCharsets.UTF_8));
    98→
    99→            // Combine IV and ciphertext
   100→            ByteBuffer byteBuffer = ByteBuffer.allocate(iv.length + cipherText.length);
   101→            byteBuffer.put(iv);
   102→            byteBuffer.put(cipherText);
   103→
   104→            return byteBuffer.array();
   105→
   106→        } catch (Exception e) {
   107→            log.error("Encryption failed", e);
   108→            throw new EncryptionException("Failed to encrypt data", e);
   109→        }
   110→    }
   111→
   112→    /**
   113→     * Decrypts data that was encrypted with the encrypt method.
   114→     * Expects the IV to be prepended to the ciphertext.
   115→     *
   116→     * @param encryptedData the encrypted data (IV + ciphertext)
   117→     * @return the decrypted plain text
   118→     * @throws EncryptionException if decryption fails
   119→     */
   120→    public String decrypt(byte[] encryptedData) {
   121→        if (encryptedData == null) {
   122→            return null;
   123→        }
   124→
   125→        if (!encryptionEnabled) {
   126→            // Return plain string when encryption is disabled (dev mode)
   127→            return new String(encryptedData, StandardCharsets.UTF_8);
   128→        }
   129→
   130→        try {
   131→            // Extract IV and ciphertext
   132→            ByteBuffer byteBuffer = ByteBuffer.wrap(encryptedData);
   133→
   134→            byte[] iv = new byte[GCM_IV_LENGTH];
   135→            byteBuffer.get(iv);
   136→
   137→            byte[] cipherText = new byte[byteBuffer.remaining()];
   138→            byteBuffer.get(cipherText);
   139→
   140→            // Initialize cipher
   141→            Cipher cipher = Cipher.getInstance(ALGORITHM);
   142→            GCMParameterSpec parameterSpec = new GCMParameterSpec(GCM_TAG_LENGTH, iv);
   143→            cipher.init(Cipher.DECRYPT_MODE, secretKey, parameterSpec);
   144→
   145→            // Decrypt
   146→            byte[] plainText = cipher.doFinal(cipherText);
   147→
   148→            return new String(plainText, StandardCharsets.UTF_8);
   149→
   150→        } catch (Exception e) {
   151→            log.error("Decryption failed", e);
   152→            throw new EncryptionException("Failed to decrypt data", e);
   153→        }
   154→    }
   155→
   156→    /**
   157→     * Encrypts data and returns it as a Base64 encoded string.
   158→     * Useful for storing in text-based formats.
   159→     *
   160→     * @param plainText the text to encrypt
   161→     * @return Base64 encoded encrypted data
   162→     */
   163→    public String encryptToBase64(String plainText) {
   164→        byte[] encrypted = encrypt(plainText);
   165→        return encrypted != null ? Base64.getEncoder().encodeToString(encrypted) : null;
   166→    }
   167→
   168→    /**
   169→     * Decrypts Base64 encoded encrypted data.
   170→     *
   171→     * @param base64EncryptedData the Base64 encoded encrypted data
   172→     * @return the decrypted plain text
   173→     */
   174→    public String decryptFromBase64(String base64EncryptedData) {
   175→        if (base64EncryptedData == null || base64EncryptedData.isBlank()) {
   176→            return null;
   177→        }
   178→        byte[] encryptedData = Base64.getDecoder().decode(base64EncryptedData);
   179→        return decrypt(encryptedData);
   180→    }
   181→
   182→    /**
   183→     * Generates a new encryption key suitable for AES-256.
   184→     * Useful for initial key generation during setup.
   185→     *
   186→     * @return Base64 encoded 256-bit key
   187→     */
   188→    public static String generateKey() {
   189→        byte[] key = new byte[32]; // 256 bits
   190→        new SecureRandom().nextBytes(key);
   191→        return Base64.getEncoder().encodeToString(key);
   192→    }
   193→
   194→    /**
   195→     * Checks if encryption is properly configured.
   196→     *
   197→     * @return true if encryption is enabled and configured
   198→     */
   199→    public boolean isEncryptionConfigured() {
   200→        return encryptionEnabled && encryptionKeyBase64 != null && !encryptionKeyBase64.isBlank();
   201→    }
   202→
   203→    /**
   204→     * Custom exception for encryption/decryption errors.
   205→     */
   206→    public static class EncryptionException extends RuntimeException {
   207→        public EncryptionException(String message, Throwable cause) {
   208→            super(message, cause);
   209→        }
   210→
   211→        public EncryptionException(String message) {
   212→            super(message);
   213→        }
   214→    }
   215→}
