import io.swagger.v3.oas.annotations.media.Schema;

/**
 * Enum representing different types of Git webhook events.
 * Used to identify and handle various webhook events from Git providers.
 */
@Schema(description = "Git webhook event types")
public enum GitWebhookEvent {

    /**
     * Push event - code pushed to repository.
     * Supported by: GitHub, GitLab, Azure DevOps, Bitbucket
     */
    @Schema(description = "Push event triggered when code is pushed to repository")
    PUSH,

    /**
     * Pull request opened event.
     * Supported by: GitHub (pull_request), GitLab (merge_request), Azure DevOps (pullrequest.created)
     */
    @Schema(description = "Pull/Merge request opened")
    PULL_REQUEST_OPENED,

    /**
     * Pull request merged event.
     * Supported by: GitHub, GitLab, Azure DevOps
     */
    @Schema(description = "Pull/Merge request merged")
    PULL_REQUEST_MERGED,

    /**
     * Pull request closed event.
     * Supported by: GitHub, GitLab, Azure DevOps
     */
    @Schema(description = "Pull/Merge request closed")
    PULL_REQUEST_CLOSED,

    /**
     * Tag created event.
     * Supported by: GitHub, GitLab, Azure DevOps
     */
    @Schema(description = "Tag created in repository")
    TAG_CREATED,

    /**
     * Branch created event.
     * Supported by: GitHub, GitLab, Azure DevOps
     */
    @Schema(description = "Branch created in repository")
    BRANCH_CREATED,

    /**
     * Branch deleted event.
     * Supported by: GitHub, GitLab, Azure DevOps
     */
    @Schema(description = "Branch deleted from repository")
    BRANCH_DELETED,

    /**
     * Repository created event.
     * Supported by: GitHub, GitLab
     */
    @Schema(description = "Repository created")
    REPOSITORY_CREATED,

    /**
     * Unknown or unsupported event type.
     */
    @Schema(description = "Unknown or unsupported event type")
    UNKNOWN;

    /**
     * Parse webhook event from GitHub event header.
     *
     * @param githubEvent the X-GitHub-Event header value
     * @return the corresponding GitWebhookEvent
     */
    public static GitWebhookEvent fromGitHub(String githubEvent) {
        if (githubEvent == null) {
            return UNKNOWN;
        }

        return switch (githubEvent.toLowerCase()) {
            case "push" -> PUSH;
            case "pull_request" -> PULL_REQUEST_OPENED; // Further refined by action in payload
            case "create" -> BRANCH_CREATED;
            case "delete" -> BRANCH_DELETED;
            case "repository" -> REPOSITORY_CREATED;
            default -> UNKNOWN;
        };
    }

    /**
     * Parse webhook event from GitLab event header.
     *
     * @param gitlabEvent the X-Gitlab-Event header value
     * @return the corresponding GitWebhookEvent
     */
    public static GitWebhookEvent fromGitLab(String gitlabEvent) {
        if (gitlabEvent == null) {
            return UNKNOWN;
        }

        return switch (gitlabEvent.toLowerCase()) {
            case "push hook" -> PUSH;
            case "merge request hook" -> PULL_REQUEST_OPENED; // Further refined by action
            case "tag push hook" -> TAG_CREATED;
            default -> UNKNOWN;
        };
    }

    /**
     * Parse webhook event from Azure DevOps event type.
     *
     * @param azureEvent the event type from payload
     * @return the corresponding GitWebhookEvent
     */
    public static GitWebhookEvent fromAzureDevOps(String azureEvent) {
        if (azureEvent == null) {
            return UNKNOWN;
        }

        return switch (azureEvent.toLowerCase()) {
            case "git.push" -> PUSH;
            case "git.pullrequest.created" -> PULL_REQUEST_OPENED;
            case "git.pullrequest.merged" -> PULL_REQUEST_MERGED;
            case "git.pullrequest.updated" -> PULL_REQUEST_CLOSED;
            default -> UNKNOWN;
        };
    }
}

