     1→import apiClient from './client';
     2→import {
     3→  TenantGitConfig,
     4→  GitConfigRequest,
     5→  GitConfigResponse,
     6→  GitConnectionTestResult,
     7→  SyncTriggerRequest,
     8→  SyncTriggerResponse,
     9→  GitSyncHistoryResponse,
    10→} from '../types/gitSync';
    11→
    12→export interface SyncHistoryFilters {
    13→  packageId?: string;
    14→  status?: string;
    15→  syncType?: string;
    16→  page?: number;
    17→  size?: number;
    18→}
    19→
    20→export const gitSyncApi = {
    21→  // ==================== Configuration ====================
    22→
    23→  /**
    24→   * Get current Git configuration for the tenant
    25→   * Returns configured: false if not set up
    26→   */
    27→  getConfig: async (): Promise<GitConfigResponse> => {
    28→    const response = await apiClient.get<GitConfigResponse>('/settings/git');
    29→    return response.data;
    30→  },
    31→
    32→  /**
    33→   * Create new Git configuration for the tenant
    34→   * Only one config per tenant is allowed
    35→   */
    36→  createConfig: async (data: GitConfigRequest): Promise<TenantGitConfig> => {
    37→    const response = await apiClient.post<TenantGitConfig>('/settings/git', data);
    38→    return response.data;
    39→  },
    40→
    41→  /**
    42→   * Update existing Git configuration
    43→   */
    44→  updateConfig: async (data: GitConfigRequest): Promise<TenantGitConfig> => {
    45→    const response = await apiClient.put<TenantGitConfig>('/settings/git', data);
    46→    return response.data;
    47→  },
    48→
    49→  /**
    50→   * Delete Git configuration (disables sync)
    51→   */
    52→  deleteConfig: async (): Promise<void> => {
    53→    await apiClient.delete('/settings/git');
    54→  },
    55→
    56→  // ==================== Connection Testing ====================
    57→
    58→  /**
    59→   * Test Git connection with current or new credentials
    60→   * Can be used before saving config to validate
    61→   */
    62→  testConnection: async (data: GitConfigRequest): Promise<GitConnectionTestResult> => {
    63→    const response = await apiClient.post<GitConnectionTestResult>('/settings/git/test', data);
    64→    return response.data;
    65→  },
    66→
    67→  // ==================== Sync Operations ====================
    68→
    69→  /**
    70→   * Trigger a manual sync operation
    71→   * Can optionally specify a package/version to sync
    72→   */
    73→  triggerSync: async (data: SyncTriggerRequest = {}): Promise<SyncTriggerResponse> => {
    74→    const response = await apiClient.post<SyncTriggerResponse>('/settings/git/sync', data);
    75→    return response.data;
    76→  },
    77→
    78→  /**
    79→   * Get sync history with optional filters
    80→   */
    81→  getSyncHistory: async (filters: SyncHistoryFilters = {}): Promise<GitSyncHistoryResponse> => {
    82→    const params = new URLSearchParams();
    83→    if (filters.packageId) params.append('packageId', filters.packageId);
    84→    if (filters.status) params.append('status', filters.status);
    85→    if (filters.syncType) params.append('syncType', filters.syncType);
    86→    params.append('page', String(filters.page || 0));
    87→    params.append('size', String(filters.size || 20));
    88→
    89→    const response = await apiClient.get<GitSyncHistoryResponse>(`/settings/git/history?${params}`);
    90→    return response.data;
    91→  },
    92→};
    93→

