     1→import apiClient, { getErrorMessage } from './client';
     2→import {
     3→  GitImportRequest,
     4→  GitImportResponse,
     5→  ConflictResolutionRequest,
     6→} from '../types/gitImport';
     7→
     8→/**
     9→ * API Client for Git Import Operations
    10→ *
    11→ * Provides functions to:
    12→ * - Trigger imports from Git repository
    13→ * - Check import operation status
    14→ * - Resolve merge conflicts
    15→ */
    16→
    17→const GIT_IMPORT_BASE = '/settings/git/import';
    18→
    19→/**
    20→ * Trigger a Git import operation.
    21→ * Imports templates from Git repository to blob storage.
    22→ *
    23→ * @param request - Import request containing branch name and optional filters
    24→ * @returns Promise with import response including operation ID and status
    25→ */
    26→export const triggerImport = async (
    27→  request: GitImportRequest
    28→): Promise<GitImportResponse> => {
    29→  try {
    30→    const response = await apiClient.post<GitImportResponse>(
    31→      GIT_IMPORT_BASE,
    32→      request
    33→    );
    34→    return response.data;
    35→  } catch (error) {
    36→    throw new Error(getErrorMessage(error));
    37→  }
    38→};
    39→
    40→/**
    41→ * Get the status of a Git import operation.
    42→ *
    43→ * @param importId - The import operation ID returned from triggerImport
    44→ * @returns Promise with import response including current status and results
    45→ */
    46→export const getImportStatus = async (
    47→  importId: string
    48→): Promise<GitImportResponse> => {
    49→  try {
    50→    const response = await apiClient.get<GitImportResponse>(
    51→      `${GIT_IMPORT_BASE}/${importId}`
    52→    );
    53→    return response.data;
    54→  } catch (error) {
    55→    throw new Error(getErrorMessage(error));
    56→  }
    57→};
    58→
    59→/**
    60→ * Resolve conflicts detected during import.
    61→ * Applies resolution strategies to merge conflicts.
    62→ *
    63→ * @param request - Conflict resolution request with strategies for each conflict
    64→ * @returns Promise with updated import response after conflict resolution
    65→ */
    66→export const resolveConflicts = async (
    67→  request: ConflictResolutionRequest
    68→): Promise<GitImportResponse> => {
    69→  try {
    70→    const response = await apiClient.post<GitImportResponse>(
    71→      `${GIT_IMPORT_BASE}/resolve-conflicts`,
    72→      request
    73→    );
    74→    return response.data;
    75→  } catch (error) {
    76→    throw new Error(getErrorMessage(error));
    77→  }
    78→};
    79→

